      break;
  }
  const { fontSize, ref } = useFitText({ maxFontSize: 500, minFontSize: 100 });
  return (
    <div>
    <Header mainText={"Event "+ eId} showBack={true}/>
      <div className="status">
        <p className="bold">Registration Status: </p>
        <p>{UserEvents[i]["status"]} </p>
